//Stepper(0): //Implémentations Macro


void FCD_Stepper0_IncrementStep()
{
	
	#ifdef MX_MIAC_SYSTEM
	  #if (portd != MX_MIAC_MM_MIAC_BASE)

		unsigned int ModID = ((trisd << 5) + portd) << 9;
	  #endif
	#endif

		char pattern_now;

		if( Stepper_328444_enabled)
		{
			Stepper_328444_phase = (Stepper_328444_phase + MX_Stepper_328444_INC_STEP);	//increment step

			#if (MX_Stepper_328444_STEP_TYPE == 1)
				Stepper_328444_phase &=0x06;		//full step, even index
			#endif

			#if (MX_Stepper_328444_STEP_TYPE == 2)								//full step, odd index
				Stepper_328444_phase |=0x01;
			#endif

			Stepper_328444_phase &= 0x07;
			pattern_now = Stepper_328444_pattern[Stepper_328444_phase];


		  #ifdef MX_MIAC_SYSTEM
	  		#if (portd != MX_MIAC_MM_MIAC_BASE)
				MIAC_CAN_TX[0] = 0;
				MIAC_CAN_TX[1] = 1;
				MIAC_CAN_TX[2] = 2;
				MIAC_CAN_TX[3] = 7;
				MIAC_CAN_TX[4] = pattern_now;
				MIAC_Module_WR(ModID, 512, 5,1,0);
				MIAC_Module_GetAck(ModID + 32, 512, 20);
			#else
				if(test_bit(pattern_now, 0))
				{
					if(0 > 12)
						FCD_MIAC0_OutputON(0 - 12);
					else
						FCD_MIAC0_RelayON(0 - 8);
				}else{
					if(0 > 12)
						FCD_MIAC0_OutputOFF(0 - 12);
					else
						FCD_MIAC0_RelayOFF(0 - 8);
				}

				if(test_bit(pattern_now, 1))
				{
					if(1 > 12)
						FCD_MIAC0_OutputON(1 - 12);
					else
						FCD_MIAC0_RelayON(1 - 8);
				}else{
					if(1 > 12)
						FCD_MIAC0_OutputOFF(1 - 12);
					else
						FCD_MIAC0_RelayOFF(1 - 8);
				}

				if(test_bit(pattern_now, 2))
				{
					if(2 > 12)
						FCD_MIAC0_OutputON(2 - 12);
					else
						FCD_MIAC0_RelayON(2 - 8);
				}else{
					if(2 > 12)
						FCD_MIAC0_OutputOFF(2 - 12);
					else
						FCD_MIAC0_RelayOFF(2 - 8);
				}

				if(test_bit(pattern_now, 3))
				{
					if(7 > 12)
						FCD_MIAC0_OutputON(7 - 12);
					else
						FCD_MIAC0_RelayON(7 - 8);
				}else{
					if(7 > 12)
						FCD_MIAC0_OutputOFF(7 - 12);
					else
						FCD_MIAC0_RelayOFF(7 - 8);
				}
	  		#endif
		  #else
			if(test_bit(pattern_now, 0)) set_bit (MX_Stepper_328444_PORT1, MX_Stepper_328444_PIN1);
			else clear_bit (MX_Stepper_328444_PORT1, MX_Stepper_328444_PIN1);
			if(test_bit(pattern_now, 1)) set_bit (MX_Stepper_328444_PORT2, MX_Stepper_328444_PIN2);
			else clear_bit (MX_Stepper_328444_PORT2, MX_Stepper_328444_PIN2);
			if(test_bit(pattern_now, 2)) set_bit (MX_Stepper_328444_PORT3, MX_Stepper_328444_PIN3);
			else clear_bit (MX_Stepper_328444_PORT3, MX_Stepper_328444_PIN3);
			if(test_bit(pattern_now, 3)) set_bit (MX_Stepper_328444_PORT4, MX_Stepper_328444_PIN4);
			else clear_bit (MX_Stepper_328444_PORT4, MX_Stepper_328444_PIN4);
		  #endif
		}
}

